package test02;

public class Fortest2_0106 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int times=2; times<=9; times++) {
			for(int dan=1; dan<=9; dan++){
				System.out.print(dan+"X"+times+"="+dan*times+"\t");
			}
			System.out.println();
		}

	}

}

//2x1=2
//2x2=4   이런식으로 나오게 표시
